mapper.select:
select
  oid, uid, onumber, wnumber, itemtype, remark, weight, otime, ystatus, money
from yto.myorder


        and oid = #{oid}


        and uid = #{uid}


        and onumber = #{onumber}


        and wnumber = #{wnumber}


        and itemtype = #{itemtype}


        and remark = #{remark}


        and weight = #{weight}


        and otime = #{otime}


        and ystatus = #{ystatus}


        and money = #{money}

mapper.update:
update yto.myorder
set
    ystatus=#{status}
where
    onumber=#{onumber}
